import { ILogtailLog } from "@logtail/types";
/**
 * batch the buffer coming in, process them and then resolve
 *
 * @param size - Number
 * @param flushTimeout - Number
 */
export default function makeBatch(size?: number, flushTimeout?: number): (fn: Function) => (log: ILogtailLog) => Promise<ILogtailLog>;
