import { ILogtailLog } from "@logtail/types";
/**
 * tries to process logs maximum 3 times in case of server error.
 *
 * @param fn - (logs: ILogtailLog[]) => Promise<ILogtailLog[]>
 */
export default function makeRetry(fn: (logs: ILogtailLog[]) => Promise<ILogtailLog[]>): Promise<(logs: ILogtailLog[]) => Promise<ILogtailLog[]>>;
